mapper.delete:
        delete from user     
        where
	        user_mail = #{user_mail} and user_password = #{user_password}

mapper.update:
    	update user set
    		user_password = #{user_password},
    		user_nickname = #{user_nickname},
	    	user_phonenumber = #{user_phonenumber},
	    	user_self = #{user_self}
	    where user_mail = #{user_mail}

mapper.select:
select user_mail, user_password
  from user
 where user_mail = #{user_mail} and user_password = #{user_password};

select user_mail
  from user
 where user_mail = #{user_mail};

select user_nickname
  from user
 where user_nickname = #{user_nickname};

select user_mail
  from user
 where user_name = #{user_name} and user_option = #{user_option};

select user_password
  from user
 where user_mail = #{user_mail} and user_option = #{user_option};
